import React, { useState } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import "./App.css";

const columns = {
  todo: "To Do",
  inProgress: "In Progress",
  done: "Done",
};

const columnKeys = Object.keys(columns);

function App() {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState("todo");

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (editingTaskId !== null) {
      setTasks((prev) =>
        prev.map((task) =>
          task.id === editingTaskId
            ? { ...task, text: taskText, column: selectedColumn }
            : task
        )
      );
      setEditingTaskId(null);
    } else {
      const newTask = {
        id: Date.now().toString(),
        text: taskText,
        column: selectedColumn,
      };
      setTasks([...tasks, newTask]);
    }
    setTaskText("");
  };

  const handleDelete = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const handleEdit = (task) => {
    setTaskText(task.text);
    setEditingTaskId(task.id);
    setSelectedColumn(task.column);
  };

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    )
      return;

    const movedTask = tasks.find((t) => t.id === draggableId);
    const updatedTask = { ...movedTask, column: destination.droppableId };

    const updatedTasks = tasks.filter((t) => t.id !== draggableId);
    updatedTasks.splice(destination.index, 0, updatedTask);
    setTasks(updatedTasks);
  };

  return (
    <div className="App" style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <h1 style={{ textAlign: "center" }}>Kanban Board</h1>
      <form
        onSubmit={handleAddOrUpdate}
        style={{
          marginBottom: "20px",
          display: "flex",
          gap: "10px",
          justifyContent: "center",
        }}
      >
        <input
          type="text"
          placeholder="Task"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          required
          style={{ padding: "8px", borderRadius: "4px", border: "1px solid #ccc" }}
        />
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
          style={{ padding: "8px", borderRadius: "4px", border: "1px solid #ccc" }}
        >
          {columnKeys.map((colKey) => (
            <option key={colKey} value={colKey}>
              {columns[colKey]}
            </option>
          ))}
        </select>
        <button
          type="submit"
          style={{
            padding: "8px 16px",
            borderRadius: "4px",
            border: "none",
            backgroundColor: "#007bff",
            color: "#fff",
            cursor: "pointer",
          }}
        >
          {editingTaskId ? "Update" : "Add"}
        </button>
      </form>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="board" style={{ display: "flex", gap: "20px", justifyContent: "center" }}>
          {columnKeys.map((colKey) => (
            <Droppable key={colKey} droppableId={colKey}>
              {(provided) => (
                <div
                  className="column"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    backgroundColor: "#f9f9f9",
                    padding: "16px",
                    borderRadius: "8px",
                    minHeight: "300px",
                    width: "300px",
                    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
                  }}
                >
                  <h2 style={{ textAlign: "center", marginBottom: "16px" }}>
                    {columns[colKey]}
                  </h2>
                  {tasks
                    .filter((task) => task.column === colKey)
                    .map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => (
                          <div
                            className="card"
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              padding: "12px",
                              backgroundColor: "#fff",
                              borderRadius: "6px",
                              marginBottom: "10px",
                              boxShadow: "0 1px 4px rgba(0,0,0,0.1)",
                              position: "relative",
                              ...provided.draggableProps.style,
                            }}
                          >
                            <p style={{ marginBottom: "8px" }}>{task.text}</p>
                            {colKey === "inProgress" && (
                              <label style={{ display: "flex", alignItems: "center", gap: "6px" }}>
                                <input
                                  type="checkbox"
                                  onChange={() =>
                                    setTasks((prev) =>
                                      prev.map((t) =>
                                        t.id === task.id
                                          ? { ...t, column: "done" }
                                          : t
                                      )
                                    )
                                  }
                                />
                                Move to Done
                              </label>
                            )}
                            <div className="actions" style={{ marginTop: "8px", display: "flex", gap: "8px" }}>
                              <button
                                onClick={() => handleEdit(task)}
                                style={{
                                  border: "none",
                                  background: "none",
                                  cursor: "pointer",
                                  fontSize: "16px",
                                }}
                              >
                                ‚úèÔ∏è
                              </button>
                              <button
                                onClick={() => handleDelete(task.id)}
                                style={{
                                  border: "none",
                                  background: "none",
                                  cursor: "pointer",
                                  fontSize: "16px",
                                }}
                              >
                                üóë
                              </button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}

export default App;
