import React, { useState } from "react";
import "./App.css";

const columns = {
  todo: "To Do",
  inProgress: "In Progress",
  done: "Done",
};

const columnKeys = Object.keys(columns);

function App() {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState("");
  const [taskDeadline, setTaskDeadline] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState("todo");
  const [theme, setTheme] = useState("light");

  const handleAddOrUpdate = (e) => {
    e.preventDefault();

    if (editingTaskId !== null) {
      setTasks((prev) =>
        prev.map((task) =>
          task.id === editingTaskId
            ? {
                ...task,
                text: taskText,
                deadline: taskDeadline,
                column: selectedColumn,
              }
            : task
        )
      );
      setEditingTaskId(null);
    } else {
      const newTask = {
        id: Date.now().toString(),
        text: taskText,
        deadline: taskDeadline,
        column: selectedColumn,
      };
      setTasks([...tasks, newTask]);
    }

    setTaskText("");
    setTaskDeadline("");
    setSelectedColumn("todo");
  };

  const handleDelete = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const handleEdit = (task) => {
    setTaskText(task.text);
    setTaskDeadline(task.deadline);
    setEditingTaskId(task.id);
    setSelectedColumn(task.column);
  };

  const handleCheckboxChange = (task) => {
    const nextColumn =
      task.column === "todo" ? "inProgress" : task.column === "inProgress" ? "done" : task.column;

    setTasks((prev) =>
      prev.map((t) => (t.id === task.id ? { ...t, column: nextColumn } : t))
    );
  };

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  return (
    <div className={`App ${theme}`}>
      <div className="header">
        <h1>Kanban Board</h1>
        <button className="theme-button" onClick={toggleTheme}>
          {theme === "light" ? "üåôDark" : "üåûLight"} Theme
        </button>
      </div>

      <form className="task-form" onSubmit={handleAddOrUpdate}>
        <input
          type="text"
          placeholder="Task"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          required
        />
        <input
          type="date"
          value={taskDeadline}
          onChange={(e) => setTaskDeadline(e.target.value)}
          required={selectedColumn !== "done"}
        />
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
        >
          {columnKeys.map((colKey) => (
            <option key={colKey} value={colKey}>
              {columns[colKey]}
            </option>
          ))}
        </select>
        <button type="submit">{editingTaskId ? "Update" : "Add"}</button>
      </form>

      <div className="board">
        {columnKeys.map((colKey) => (
          <div className="column" key={colKey}>
            <h2>{columns[colKey]}</h2>
            {tasks
              .filter((task) => task.column === colKey)
              .map((task) => {
                const isDeadlineMissed =
                  task.deadline &&
                  new Date(task.deadline) < new Date() &&
                  task.column !== "done";

                return (
                  <div
                    key={task.id}
                    className="card"
                    style={{
                      backgroundColor: isDeadlineMissed ? "#ffe6e6" : undefined,
                    }}
                  >
                    <div className="card-header">
                      {(colKey === "todo" || colKey === "inProgress") && (
                        <input
                          type="checkbox"
                          onChange={() => handleCheckboxChange(task)}
                        />
                      )}
                      <p>{task.text}</p>
                    </div>

                    <p className="deadline">Deadline: {task.deadline}</p>

                    {isDeadlineMissed && (
                      <p className="warning">
                        ‚ö†Ô∏è Missed Deadline!
                      </p>
                    )}

                    <div className="actions">
                      <button onClick={() => handleEdit(task)}>‚úèÔ∏è</button>
                      <button onClick={() => handleDelete(task.id)}>üóë</button>
                    </div>
                  </div>
                );
              })}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
