import React, { useState } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import "./App.css";

const initialColumns = ["To Do", "In Progress", "Done"];

function App() {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState("To Do");

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (editingTaskId !== null) {
      setTasks((prev) =>
        prev.map((task) =>
          task.id === editingTaskId
            ? { ...task, text: taskText, column: selectedColumn }
            : task
        )
      );
      setEditingTaskId(null);
    } else {
      const newTask = {
        id: Date.now().toString(),
        text: taskText,
        column: selectedColumn,
      };
      setTasks([...tasks, newTask]);
    }
    setTaskText("");
  };

  const handleDelete = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const handleEdit = (task) => {
    setTaskText(task.text);
    setEditingTaskId(task.id);
    setSelectedColumn(task.column);
  };

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    )
      return;

    const movedTask = tasks.find((t) => t.id === draggableId);
    const updatedTask = { ...movedTask, column: destination.droppableId };

    const updatedTasks = tasks.filter((t) => t.id !== draggableId);
    updatedTasks.splice(destination.index, 0, updatedTask);
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <h1>Kanban Board</h1>
      <form onSubmit={handleAddOrUpdate}>
        <input
          type="text"
          placeholder="Task"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          required
        />
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
        >
          {initialColumns.map((col) => (
            <option key={col} value={col}>
              {col}
            </option>
          ))}
        </select>
        <button type="submit">{editingTaskId ? "Update" : "Add"}</button>
      </form>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="board" style={{ display: "flex", gap: "20px" }}>
          {initialColumns.map((col) => (
            <Droppable key={col} droppableId={col}>
              {(provided) => (
                <div
                  className="column"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    backgroundColor: "#f4f4f4",
                    padding: "10px",
                    borderRadius: "8px",
                    minHeight: "200px",
                    width: "250px",
                  }}
                >
                  <h2>{col}</h2>
                  {tasks
                    .filter((task) => task.column === col)
                    .map((task, index) => (
                      <Draggable
                        key={task.id}
                        draggableId={task.id}
                        index={index}
                      >
                        {(provided) => (
                          <div
                            className="card"
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              padding: "10px",
                              backgroundColor: "#fff",
                              borderRadius: "4px",
                              marginBottom: "8px",
                              ...provided.draggableProps.style,
                            }}
                          >
                            <p>{task.text}</p>

                            {col === "In Progress" && (
                              <label>
                                <input
                                  type="checkbox"
                                  onChange={() =>
                                    setTasks((prev) =>
                                      prev.map((t) =>
                                        t.id === task.id
                                          ? { ...t, column: "Done" }
                                          : t
                                      )
                                    )
                                  }
                                />{" "}
                                Move to Done
                              </label>
                            )}

                            <div className="actions">
                              <button onClick={() => handleEdit(task)}>‚úèÔ∏è</button>
                              <button onClick={() => handleDelete(task.id)}>üóë</button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}

export default App;
