import React, { useState } from "react";
import "./App.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState("todo");
  const [darkMode, setDarkMode] = useState(false);

  const columns = {
    todo: "To Do",
    inProgress: "In Progress",
    done: "Done",
  };
  const columnKeys = Object.keys(columns);

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (editingTaskId !== null) {
      setTasks((prev) =>
        prev.map((task) =>
          task.id === editingTaskId
            ? { ...task, text: taskText, column: selectedColumn }
            : task
        )
      );
      setEditingTaskId(null);
    } else {
      const newTask = {
        id: Date.now().toString(),
        text: taskText,
        column: selectedColumn,
      };
      setTasks([...tasks, newTask]);
    }
    setTaskText("");
  };

  const handleDelete = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const handleEdit = (task) => {
    setTaskText(task.text);
    setEditingTaskId(task.id);
    setSelectedColumn(task.column);
  };

  const handleCheckboxMove = (task, nextColumn) => {
    setTasks((prev) =>
      prev.map((t) =>
        t.id === task.id ? { ...t, column: nextColumn } : t
      )
    );
  };

  return (
    <div className={`App ${darkMode ? "dark" : "light"}`}>
      <header className="header">
        <h1>Kanban Board</h1>
        <button className="theme-button" onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
        </button>
      </header>

      <form onSubmit={handleAddOrUpdate} className="task-form">
        <input
          type="text"
          placeholder="Task"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          required
        />
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
        >
          {columnKeys.map((colKey) => (
            <option key={colKey} value={colKey}>
              {columns[colKey]}
            </option>
          ))}
        </select>
        <button type="submit">{editingTaskId ? "Update" : "Add"}</button>
      </form>

      <div className="board">
        {columnKeys.map((colKey) => (
          <div key={colKey} className="column">
            <h2>{columns[colKey]}</h2>
            {tasks
              .filter((task) => task.column === colKey)
              .map((task, index) => (
                <div key={task.id} className="card">
                  <div className="card-header">
                    {(colKey === "todo" || colKey === "inProgress") && (
                      <input
                        type="checkbox"
                        onChange={() =>
                          handleCheckboxMove(
                            task,
                            colKey === "todo" ? "inProgress" : "done"
                          )
                        }
                      />
                    )}
                    <p>{task.text}</p>
                  </div>
                  <div className="actions">
                    <button onClick={() => handleEdit(task)}>‚úèÔ∏è</button>
                    <button onClick={() => handleDelete(task.id)}>üóë</button>
                  </div>
                  {index !== tasks.filter((t) => t.column === colKey).length - 1 && (
                    <hr />
                  )}
                </div>
              ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
