import React, { useState } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import "./App.css";

const columns = {
  todo: "To Do",
  inProgress: "In Progress",
  done: "Done",
};

const columnKeys = Object.keys(columns);

function App() {
  const [tasks, setTasks] = useState([]);
  const [taskText, setTaskText] = useState("");
  const [editingTaskId, setEditingTaskId] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState("todo");

  const handleAddOrUpdate = (e) => {
    e.preventDefault();
    if (editingTaskId !== null) {
      setTasks((prev) =>
        prev.map((task) =>
          task.id === editingTaskId
            ? { ...task, text: taskText, column: selectedColumn }
            : task
        )
      );
      setEditingTaskId(null);
    } else {
      const newTask = {
        id: Date.now().toString(),
        text: taskText,
        column: selectedColumn,
      };
      setTasks((prev) => [...prev, newTask]);
    }
    setTaskText("");
  };

  const handleDelete = (id) => {
    setTasks((prev) => prev.filter((task) => task.id !== id));
  };

  const handleEdit = (task) => {
    setTaskText(task.text);
    setEditingTaskId(task.id);
    setSelectedColumn(task.column);
  };

  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;

    const movedTask = tasks.find((task) => task.id === draggableId);
    if (!movedTask) return;

    const updatedTask = { ...movedTask, column: destination.droppableId };
    const updatedTasks = tasks.filter((task) => task.id !== draggableId);
    updatedTasks.splice(destination.index, 0, updatedTask);
    setTasks(updatedTasks);
  };

  return (
    <div className="App">
      <h1 className="title">Kanban Board</h1>
      <form className="task-form" onSubmit={handleAddOrUpdate}>
        <input
          type="text"
          placeholder="Enter a task"
          value={taskText}
          onChange={(e) => setTaskText(e.target.value)}
          required
        />
        <select
          value={selectedColumn}
          onChange={(e) => setSelectedColumn(e.target.value)}
        >
          {columnKeys.map((key) => (
            <option key={key} value={key}>
              {columns[key]}
            </option>
          ))}
        </select>
        <button type="submit">{editingTaskId ? "Update" : "Add"}</button>
      </form>

      <DragDropContext onDragEnd={onDragEnd}>
        <div className="board">
          {columnKeys.map((colKey) => (
            <Droppable key={colKey} droppableId={colKey}>
              {(provided) => (
                <div
                  className="column"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                >
                  <h2>{columns[colKey]}</h2>
                  {tasks
                    .filter((task) => task.column === colKey)
                    .map((task, index) => (
                      <Draggable
                        key={task.id}
                        draggableId={task.id}
                        index={index}
                      >
                        {(provided) => (
                          <div
                            className="card"
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                          >
                            <p>{task.text}</p>
                            {colKey === "inProgress" && (
                              <label>
                                <input
                                  type="checkbox"
                                  onChange={() =>
                                    setTasks((prev) =>
                                      prev.map((t) =>
                                        t.id === task.id
                                          ? { ...t, column: "done" }
                                          : t
                                      )
                                    )
                                  }
                                />
                                Move to Done
                              </label>
                            )}
                            <div className="actions">
                              <button onClick={() => handleEdit(task)}>‚úèÔ∏è</button>
                              <button onClick={() => handleDelete(task.id)}>üóë</button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}

export default App;
